var CodeCleaner=function(){function e(){}return e.prototype.removeTagsFromArray=function(e,r){for(var t=0;t<e.length;t++)r=(r=r.replace(this.tagRegBuilder(e[t]),"")).replace(this.tagCloseRegBuilder(e[t]),"");return r},e.prototype.removeAttribsFromArray=function(e,r){for(var t=0;t<e.length;t++)r=r.replace(this.attrRegBuilder(e[t]),"");return r},e.prototype.attrRegBuilder=function(e){return RegExp("([\\s]{0,}"+e+'\\=[\\"\\\'^\\>^\\<]{1}[^\\>^\\<^\\".*]{0,}[\\"\\\'^\\>^\\<]{1})',"gi")},e.prototype.tagRegBuilder=function(e){return RegExp("(\\<"+e+"[^\\>]{0,}\\>)","gi")},e.prototype.tagCloseRegBuilder=function(e){return RegExp("(\\<\\/"+e+"[^\\>]{0,}\\>)","gi")},e}();
